'use client'

import React, { useState } from 'react'
import { Plus_Jakarta_Sans } from 'next/font/google'
import { usePathname } from 'next/navigation'
import Link from 'next/link'

const jakarta = Plus_Jakarta_Sans({
  subsets: ['latin'],
  weight: ['400', '500', '600', '700'],
})

export default function DashboardLayout({ children }: { children: React.ReactNode }) {
  const [sidebarOpen, setSidebarOpen] = useState(true)
  const pathname = usePathname()

  const isActive = (path: string) => pathname === path

  return (
    <div className={`${jakarta.className} min-h-screen bg-white text-slate-900`}>
      <div className="flex min-h-screen">
        <aside
          className={`flex flex-col border-r border-slate-200 bg-white px-5 py-5 transition-all duration-300 ${
            sidebarOpen ? 'w-56' : 'w-20'
          }`}
        >
          <div className="mb-6 flex items-center justify-between">
            {sidebarOpen && (
              <span className="text-lg font-semibold text-slate-900">Dashboard</span>
            )}
            <button
              onClick={() => setSidebarOpen((prev) => !prev)}
              className="cursor-pointer rounded-lg p-2 transition hover:bg-slate-100"
            >
              <SplitIcon className="h-5 w-5" />
            </button>
          </div>

          <div className="flex-1">
            <div className="space-y-1">
              <Link
                href="/dashboard/content"
                className={`flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm font-medium transition hover:bg-slate-50 ${
                  isActive('/dashboard/content') ? 'text-blue-600 bg-blue-50' : 'text-slate-500'
                }`}
              >
                <ContentIcon className="h-4 w-4 text-slate-400" />
                {sidebarOpen && 'Content'}
              </Link>

              <div className="ml-4 space-y-1">
                {sidebarOpen && (
                  <>
                    <div className="flex items-center justify-between py-1 text-xs text-slate-400">
                      <span>About</span>
                      <PencilIcon className="h-3 w-3" />
                    </div>
                    <div className="flex items-center justify-between py-1 text-xs text-slate-400">
                      <span>Portfolio</span>
                      <PencilIcon className="h-3 w-3" />
                    </div>
                    <div className="flex items-center justify-between py-1 text-xs text-slate-400">
                      <span>Services</span>
                      <PencilIcon className="h-3 w-3" />
                    </div>
                    <button className="flex items-center gap-2 py-1 text-xs text-blue-600">
                      <PlusIcon className="h-3 w-3" />
                      Add new page
                    </button>
                  </>
                )}
              </div>
            </div>

            <div className="mt-4 space-y-1 border-t border-slate-100 pt-4">
              <Link
                href="/dashboard/accreditation"
                className={`flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm font-medium transition hover:bg-slate-50 ${
                  isActive('/dashboard/accreditation') ? 'text-blue-600 bg-blue-50' : 'text-slate-500'
                }`}
              >
                <AwardIcon className="h-4 w-4 text-slate-400" />
                {sidebarOpen && 'Accreditation'}
              </Link>

              <Link
                href="/dashboard/testimonials"
                className={`flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm font-medium transition hover:bg-slate-50 ${
                  isActive('/dashboard/testimonials') ? 'text-blue-600 bg-blue-50' : 'text-slate-500'
                }`}
              >
                <QuoteIcon className="h-4 w-4 text-slate-400" />
                {sidebarOpen && 'Testimonials'}
              </Link>

              <Link
                href="/dashboard/forms"
                className={`flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm font-medium transition hover:bg-slate-50 ${
                  isActive('/dashboard/forms') ? 'text-blue-600 bg-blue-50' : 'text-slate-500'
                }`}
              >
                <FormIcon className="h-4 w-4 text-slate-400" />
                {sidebarOpen && 'Forms'}
              </Link>

              <Link
                href="/dashboard/emailing"
                className={`flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm font-medium transition hover:bg-slate-50 ${
                  isActive('/dashboard/emailing') ? 'text-blue-600 bg-blue-50' : 'text-slate-500'
                }`}
              >
                <MailIcon className="h-4 w-4 text-slate-400" />
                {sidebarOpen && 'Emailing'}
              </Link>

              <Link
                href="/dashboard/contact"
                className={`flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm font-medium transition hover:bg-slate-50 ${
                  isActive('/dashboard/contact') ? 'text-blue-600 bg-blue-50' : 'text-slate-500'
                }`}
              >
                <ContactIcon className="h-4 w-4 text-slate-400" />
                {sidebarOpen && 'Contact'}
              </Link>
            </div>
          </div>

          <div className="mt-auto space-y-4 border-t border-slate-100 pt-6">
            <button className="flex w-full cursor-pointer items-center gap-3 rounded-lg px-2 py-2 text-left text-sm text-slate-500 transition hover:bg-slate-50">
              <SettingsIcon className="h-4 w-4 text-slate-400" />
              {sidebarOpen && 'Settings'}
            </button>
            <div className="flex items-center gap-3 px-2">
              <div className="flex h-5 w-5 items-center justify-center rounded-full bg-[#C7E3EC] text-xs font-semibold text-[#1C7089]">
                J
              </div>
              {sidebarOpen && <span className="text-sm text-slate-600">Joel</span>}
            </div>
          </div>
        </aside>

        <main className="flex-1">{children}</main>
      </div>
    </div>
  )
}

